mapper.select:
SELECT no, mountain_no, rname, rloc, contact, menu, description, filename, likecnt, dislikecnt, mname
FROM
(SELECT ROW_NUMBER() OVER (ORDER BY no DESC) rn, no, mountain_no, rname, rloc, contact, menu, description, filename,
likecnt, dislikecnt, mname FROM res_view




)
WHERE rn BETWEEN (#{pageNo} -1) * #{amount} + 1 AND #{pageNo} * #{amount}

mapper.insert:
select seq_restaurant.nextval from dual
insert into restaurant (no, mountain_no, rname, rloc, contact, menu, description, filename) values (#{no}, (
), #{rname}, #{rloc}, #{contact}, #{menu}, #{description}, #{filename})

mapper.update:
update restaurant set mountain_no= (

), rname= #{rname}, rloc= #{rloc}, contact= #{contact}, menu= #{menu},
description= #{description}, filename = #{filename} where no = #{no}
